INSERT INTO `pages_translation` VALUES(1, 1, 'About Us', '<h3 style=\"color: #2969b0;\">GLOBIZ VENTURE CO., LTD.</h3>\r\n\r\n<h5 style=\"color: #2969b0;\">COMPANY OVERVIEW</h5>\r\n\r\n<ul class=\"list-marked\">\r\n	<li>Year 2000, Globiz Venture Co., Ltd. was established.</li>\r\n	<li>Year 2004, LA-Z-BOY USA grants license to Globiz Venture Co., Ltd. to authorize the sale and marketing for LA-Z-BOY upholstery products.</li>\r\n	<li>The company is located at 2991/12 Ladprao rd., Klongjan, Bangkapi, Bangkok 10240</li>\r\n	<li>Galleries at Sukhumvit 30/1, Mega Bangna, and Ladprao 101/3</li>\r\n	<li>Warehouse is located at Bangna-Trad 22.5 km.</li>\r\n	<li>Manpower = 73 persons</li>\r\n</ul>', 'en');
INSERT INTO `pages_translation` VALUES(2, 1, 'เกี่ยวกับเรา', '<h3 style=\"color: #2969b0;\">บริษัท โกลบิซ เวนเจอร์ จำกัด</h3>\r\n\r\n<h5 style=\"color: #2969b0;\">ข้อมูลทั่วไปเกี่ยวกับบริษัทฯ</h5>\r\n\r\n<ul class=\"list-marked\">\r\n	<li>ปี ค.ศ 2000 บริษัท โกลบิซ เวนเจอร์ จำกัด เริ่มก่อตั้ง</li>\r\n	<li>ปี ค.ศ 2004 LA-Z-BOY ของประเทศสหรัฐอเมริกาได้ให้ลิขสิทธิ์กับทางบริษัทฯ ในการเป็นตัวแทนจำหน่ายเก้าอี้และชุดโซฟาปรับเอนนอน เล-ซี-บอย ในประเทศไทยแต่เพียงผู้เดียว</li>\r\n	<li>ที่ตั้งสำนักงานใหญ่ : 2991/12 ถนนลาดพร้าว แขวงคลองจั่น เขตบางกะปิ กรุงเทพฯ 10240</li>\r\n	<li>มีแกลเลอรี่ 3 สาขา : สุขุมวิท 30/1, เมกาบางนา, และ ลาดพร้าว 101/3</li>\r\n	<li>คลังสินค้า ตั้งอยู่ที่ ถ.บางนา-ตราด กม.ที่ 22.5</li>\r\n	<li>มีพนักงานบริษัทฯ ทั้งหมด 73 คน</li>\r\n</ul>', 'th');
INSERT INTO `pages_translation` VALUES(3, 2, 'Terms & Conditions', NULL, 'en');
